import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

import InputForm from "../common/InputForm";

const RegisterCard = () => {

    const [username, setUsername] = useState(null);
    const [password, setPassword] = useState(null);
    const [errorMessage, setErrorMessage] = useState(null);

    const navigate = useNavigate();

    const usernameLabel = "username";
    const passwordLabel = "password";

    const handleLoginForm = (e) => {
        e.preventDefault();

        if(username == true){
            setErrorMessage("Username is not valid or registered!");
            return;
        }

        if(password == true){
            setErrorMessage("Password is wrong!");
            return;
        }

        navigate("/")
    }


    return ( 
        <div className={`bg-white rounded-lg px-10 md:px-16 pt-10 pb-24`}>
            <div>
                <div className={`text-2xl font-bold mb-12`}>
                    <h1>REGISTER</h1>
                </div>
                <div className={``}>
                    <form action="" method="POST">
                        <InputForm labelName={usernameLabel} />
                        <InputForm labelName={passwordLabel} />
                        <div className={`flex flex-col mt-5`}>
                            <label className="text-left" htmlFor="confirmPassword">Confirm Password</label>
                            <input className="bg-gray-200 rounded-lg py-3 px-8" name="confirmPassword" type="password" id="_confirmPassword" />
                        </div>
                        <div className={`flex flex-col mt-5`}>
                            <button className="bg-yellow-500 py-3 px-8 text-white" type="submit">REGISTER</button>
                        </div>
                        {!!errorMessage && <p>{errorMessage}</p>}
                    </form>
                </div>
            </div>
        </div>
    );
}

export default RegisterCard;
