import LevelCard from '../../components/LevelCard';
import Navbar from '../../components/Navbar';
import RoomCard from '../../components/RoomCard';
import css from './styles.module.css';

import opponents from '../../data/opponents.json';
import { useState } from 'react';

const MainPage = () => {

    const filteringRooms = (levelRoom) => {
        const filteredRooms = opponents.filter((opponent) => opponent.level === levelRoom);

        filteredRooms.forEach((filteredRoom) => {
            return (<RoomCard key={filteredRoom.id} name={filteredRoom.name} bio={filteredRoom.bio} avatar={filteredRoom.avatar} level={filteredRoom.level} createdAt={filteredRoom.createdAt} />)
        });
    }

    // const changeRooms = () => {

    // }

    const [listRoom, setListRoom] = useState([filteringRooms("GRAND MASTER")]);

    // const filteredRooms = filteringRooms("GRAND MASTER");

    // filteredRooms.forEach((filteredRoom) => {
    //     listRoom.push(<RoomCard key={filteredRoom.id} name={filteredRoom.name} bio={filteredRoom.bio} avatar={filteredRoom.avatar} level={filteredRoom.level} createdAt={filteredRoom.createdAt} />)
    // });





    return (
        <div className={`${css.gameBackground} flex flex-col`}>
            <div>
                <Navbar key="navbar"/>
            </div>
            <div className="pt-20 px-44">
                <div className="py-4">
                    <h1 className="text-2xl text-white font-bold">CHOOSE YOUR OPPONENT</h1>
                </div>
                <div className="flex flex-row py-2">
                    <button onClick={filteringRooms("NOVICE")}>
                        <LevelCard name="NOVICE" />
                    </button>
                    <LevelCard name="CLASS A" />
                    <LevelCard name="CLASS B" />
                    <LevelCard name="CLASS C" />
                    <LevelCard name="CLASS D" />
                    <LevelCard name="CANDIDATE MASTER" />
                    <LevelCard name="GRAND MASTER" />
                </div>
            </div>
            <div className="grid grid-cols-4 gap-2 pt-10 px-44">
                {/* <RoomCard name={opponents[0].name} bio={opponents[0].bio} avatar={opponents[0].avatar} level={opponents[0].level} createdAt={opponents[0].createdAt} /> */}
                {listRoom}
            </div>
            <div>

            </div>
        </div>
    );
}

export default MainPage;
